INSERT INTO [dbo].[ZnodeEmailTemplateMapper]
          ([EmailTemplateId] ,[PortalId] ,[EmailTemplateAreasId] ,[IsActive]  ,[CreatedBy] ,[CreatedDate] ,[ModifiedBy] ,[ModifiedDate] ,[IsEnableBcc])
select (select top 1 [EmailTemplateId]  from [ZnodeEmailTemplate] where [TemplateName] = 'ReturnRequestNotificationForCustomer' )
          ,PortalID ,(select top 1 [EmailTemplateAreasId] from [ZnodeEmailTemplateAreas] where [Name] = 'ReturnRequestNotificationForCustomer' )
          ,1 ,2 ,Getdate() ,2 ,Getdate() ,0
from ZnodePortal ZP
where not exists(select * from [ZnodeEmailTemplateMapper] ZETM where [EmailTemplateId] = (select top 1 [EmailTemplateId]  from [ZnodeEmailTemplate] where [TemplateName] = 'ReturnRequestNotificationForCustomer' )
      and ZP.PortalID = ZETM.PortalID and [EmailTemplateAreasId] = (select top 1 [EmailTemplateAreasId] from [ZnodeEmailTemplateAreas] where [Name] = 'ReturnRequestNotificationForCustomer' ))
GO
