--dt 10-06-2020 ZPD-10397 --> ZPD-10253
UPDATE [dbo].[ZnodeEmailTemplateLocale]
SET Content = '<p> </p><div><div style="font-family: Arial, Helvetica; font-size: 10px; text-align: left; color: #292a2a; border: solid 1px #c3c3c3; margin-top: 10px;"><div style="background-color: #eff3fb; color: #292a2a; font-size: 1.5em; font-weight: bold; padding: .5em; border-bottom: solid 1px #c3c3c3;">#StoreLogo# #SiteName# Customer Receipt</div><div style="padding: 10px;"><div style="font-family: Verdana; color: #333333; font-size: 11px;">#ReceiptText#</div><table style="font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px;" border="0" width="100%" cellspacing="3" cellpadding="0"><tbody><tr><td colspan="5"><hr /></td></tr><tr><td colspan="2" align="left" nowrap="nowrap" width="25%"><div style="color: #292a2a; font-weight: bold; font-size: 11px; padding-bottom: 5px;">Order Information</div></td><td colspan="2" align="left" nowrap="nowrap"><div style="color: #292a2a; font-weight: bold; font-size: 11px; padding-bottom: 5px;">Customer Service</div></td></tr><tr><td align="left" nowrap="nowrap" width="10%"><strong>Order Number:</strong></td><td align="left" nowrap="nowrap" width="30%">#OrderId#</td><td align="left" nowrap="nowrap" width="4%"><strong>E-Mail:</strong></td><td align="left" nowrap="nowrap">#CustomerServiceEmail#</td></tr><tr><td align="left" nowrap="nowrap"><strong>Order Date:</strong></td><td align="left" nowrap="nowrap">#OrderDate#</td><td align="left" nowrap="nowrap"><strong>Phone:</strong></td><td align="left" nowrap="nowrap">#CustomerServicePhoneNumber#</td></tr><tr><td style="vertical-align: top;" align="left" nowrap="nowrap"><strong>Promotion Code:</strong></td><td align="left" nowrap="nowrap">#PromotionCode#</td><td> </td><td> </td></tr><tr><td align="left" nowrap="nowrap"><strong>Payment Method:</strong></td><td align="left" nowrap="nowrap">#PaymentName#</td><td> </td><td> </td></tr><tr><td align="left" nowrap="nowrap"><strong>#CardTransactionLabel#</strong></td><td align="left" nowrap="nowrap">#CardTransactionID#</td><td> </td><td> </td></tr><tr><td align="left" nowrap="nowrap"><strong>#PurchaseNumberLabel#</strong></td><td align="left" nowrap="nowrap">#PONumber#</td><td> </td><td> </td></tr><tr><td align="left" nowrap="nowrap"><strong>AccountNumber:</strong></td><td align="left" nowrap="nowrap">#AccountNumber#</td><td> </td><td> </td></tr><tr><td align="left" nowrap="nowrap"><strong>ShippingMethod:</strong></td><td align="left" nowrap="nowrap">#ShippingMethod#</td><td> </td><td> </td></tr><tr><td align="left" nowrap="nowrap"><strong>Job / Project Name:</strong></td><td align="left" nowrap="nowrap">#JobName#</td><td> </td><td> </td></tr><tr><td colspan="4"><hr /></td></tr><tr><td style="font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px;" colspan="2" align="left" nowrap="nowrap" width="45%"><div style="color: #292a2a; font-weight: bold; font-size: 11px; padding-bottom: 5px;">Billing Address</div></td><td colspan="2" align="left"><div style="color: #292a2a; font-weight: bold; font-size: 11px; padding-bottom: 5px;">Shipping Address</div></td></tr><tr><td colspan="2" align="Left" nowrap="nowrap">#BillingAddress#</td><td colspan="3" valign="top">#ShippingAddress#</td></tr><tr><td align="Left" nowrap="nowrap" colspan="2">&nbsp;</td><td valign="top" colspan="3"><hr /><span style="width: 130px; display: inline-block; font-weight: bold">#LabelInHandDate#: </span>#InHandDate#<br /><span style="width: 130px; display: inline-block; font-weight: bold">#LabelShippingConstraintsCode#: </span>#ShippingConstraintCode#<br /><br /></td></tr><tr><td colspan="7"> </td></tr></tbody></table><div data-info="#AddressItems#"><table width="100%" cellspacing="0" cellpadding="3"><tbody><tr><td style="color: #292a2a; font-weight: bold; font-size: 11px; padding-bottom: 5px;" colspan="6">#ShipmentNo#</td></tr><tr><td style="color: black; font-size: 10px;" colspan="6">#ShipTo#</td></tr><tr style="background-color: #eff3fb;"><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;"><strong>SKU</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;"><strong>Item</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;"><strong>Description</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px;" align="center"><strong>Qty</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;"><strong>Price</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;"><strong>Total</strong></td></tr><tr data-info="#LineItems#OmsOrderShipmentID##"><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;" align="left" width="10%">#SKU# </td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;" align="left" width="25%">#Name#</td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;" align="left" width="25%">#Description#   <p>#ShortDescription#</p>  #UOMDescription#  <br /><br />#Column1#  </td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;" align="center" width="5%">#Quantity#</td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;" align="left" width="20%">#Price#</td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; min-width: 170px; padding: 0 5px;" align="right" width="20%">#ExtendedPrice#</td></tr><tr data-info="#AmountLineItems#OmsOrderShipmentID##"><td style="border: none; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;" colspan="5" align="right"><strong>#Title#</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; min-width: 170px; padding: 0 5px;" align="right" width="20%">#Amount#</td></tr><tr data-info="#GrandAmountLineItems#"><td style="border: none; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; padding: 0 5px;" colspan="5" align="right"><strong>#Title#</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; min-width: 170px; padding: 0 5px;" align="right" width="20%">#Amount#</td></tr><tr><td style="border: none; font-family: Verdana, Helvetica, sans-serif; color: #292a2a; font-size: 11px; padding: 0 5px 5px 5px;" colspan="5" align="right"><strong>Total</strong></td><td style="border: silver 1px solid; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px; min-width: 170px; padding: 0 5px;" align="right" width="20%"><strong>#TotalCost#</strong></td></tr></tbody></table></div><table style="width: 100%; padding: 10px; display: inline-block;" cellspacing="0" cellpadding="3"><tbody><tr><td style="border: none; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px;" colspan="2" align="left" nowrap="nowrap"><div style="color: #292a2a; font-weight: bold; font-size: 11px; padding-bottom: 5px;">#AdditionalInstructLabel#</div></td></tr><tr><td style="border: none; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px;" colspan="2"><div style="width: 675px;" align="justify">#AdditionalInstructions#</div></td></tr><tr><td style="border: none; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px;" colspan="2"><div style="margin-bottom: 5px;"> </div></td></tr><tr><td style="border: none; font-family: Verdana, Helvetica, sans-serif; color: #333333; font-size: 10px;" colspan="2" align="left" nowrap="nowrap">#FeedBack#</td></tr></tbody></table></div></div></div>'
WHERE EmailTemplateId IN (SELECT EmailTemplateId FROM [dbo].[ZnodeEmailTemplate] WHERE TemplateName = 'OrderReceipt')
AND LocaleId = 1


