insert into ZnodeEmailTemplateAreas(Name,Code,CreatedBy,CreatedDate,ModifiedBy,ModifiedDate)
select 'ContactAcknowledgment','ContactAcknowledgment',2,getdate(),2,getdate()
where not exists(select * from ZnodeEmailTemplateAreas where name = 'ContactAcknowledgment' )

insert into ZnodeEmailTemplate(TemplateName,CreatedBy,CreatedDate,ModifiedBy,ModifiedDate)
select 'ContactAcknowledgment',2,getdate(),2,getdate()
where not exists(select * from ZnodeEmailTemplate where TemplateName = 'ContactAcknowledgment' )

insert into ZnodeEmailTemplateLocale(EmailTemplateId,Subject,Descriptions,Content,LocaleId,CreatedBy,CreatedDate,ModifiedBy,ModifiedDate)
select (select top 1 EmailTemplateId from ZnodeEmailTemplate where TemplateName = 'ContactAcknowledgment' ),
'Contact Acknowledgment','Contact Acknowledgment','<!DOCTYPE html><html><body><div style="font-family: Arial, Helvetica; text-align: left; color: black; border: solid 1px black;">  <div style="background-color: #005286; font-size: 1.5em; font-weight: bold; padding-top: .5em; padding-bottom: .5em; padding-left: 1em; border-bottom: solid 1px #edffff; color: white;">#StoreLogo# New Contact Request</div>  <p style="padding: 0 1rem; margin: 10px 0px 0px; font-size: 14px; line-height: 1.714; letter-spacing: -0.005em; color: #172b4d; font-family: -apple-system, BlinkMacSystemFont, ''Segoe UI'', Roboto, Oxygen, Ubuntu, ''Fira Sans'', ''Droid Sans'', ''Helvetica Neue'', sans-serif; white-space: pre-wrap;">Team,</p>  <p style="padding: 0 1rem; margin: 10px 0px 0px; font-size: 14px; line-height: 1.714; letter-spacing: -0.005em; color: #172b4d; font-family: -apple-system, BlinkMacSystemFont, ''Segoe UI'', Roboto, Oxygen, Ubuntu, ''Fira Sans'', ''Droid Sans'', ''Helvetica Neue'', sans-serif; white-space: pre-wrap;">You have a new contact request. Please take a look at the submission received:-</p>  <p style="padding: 0 1rem; margin: 10px 0px 0px; font-size: 14px; line-height: 1.714; letter-spacing: -0.005em; color: #172b4d; font-family: -apple-system, BlinkMacSystemFont, ''Segoe UI'', Roboto, Oxygen, Ubuntu, ''Fira Sans'', ''Droid Sans'', ''Helvetica Neue'', sans-serif; white-space: pre-wrap;"><span style="color: #333333; font-family: ''Helvetica Neue'', Helvetica, Arial, sans-serif; letter-spacing: normal; white-space: normal;">DyanamicHtml</span></p>  <p style="padding: 0 1rem; margin: 10px 0px 0px; font-size: 14px; line-height: 1.714; letter-spacing: -0.005em; color: #172b4d; font-family: -apple-system, BlinkMacSystemFont, ''Segoe UI'', Roboto, Oxygen, Ubuntu, ''Fira Sans'', ''Droid Sans'', ''Helvetica Neue'', sans-serif; white-space: pre-wrap;">Thank you,<br />Admin</p>  </div></body></html>',
1,2,getdate(),2,getdate()
where not exists(select * from ZnodeEmailTemplateLocale where EmailTemplateId = (select top 1 EmailTemplateId from ZnodeEmailTemplate where TemplateName = 'ContactAcknowledgment' )
and LocaleId = 1 ) 

insert into ZnodeEmailTemplateMapper(EmailTemplateId,PortalId,EmailTemplateAreasId,IsActive,CreatedBy,CreatedDate,ModifiedBy,ModifiedDate,IsEnableBcc)
select (select top 1 EmailTemplateId from ZnodeEmailTemplate where TemplateName = 'ContactAcknowledgment' ), PortalId,
 (select top 1 EmailTemplateAreasId from ZnodeEmailTemplateAreas where Name = 'ContactAcknowledgment'),1,2,getdate(),2,getdate(),0
from ZnodePortal zp
where not exists(select * from ZnodeEmailTemplateMapper ZETM where ZETM.EmailTemplateId = (select top 1 EmailTemplateId from ZnodeEmailTemplate where TemplateName = 'ContactAcknowledgment' )
and ZETM.PortalId = zp.PortalId and EmailTemplateAreasId = (select top 1 EmailTemplateAreasId from ZnodeEmailTemplateAreas where Name = 'ContactAcknowledgment'))


