			SET @AddOnQuantity =
			(
				SELECT MAX(Quantity)
				FROM @TBL_bundleAddonRows
			);
		END;


		INSERT INTO @TBL_bundleAddonRows( RowId, ParentOmsSavedCartLineItemId, SKU, Quantity, OrderLineItemRelationshipTypeID, CustomText, CartAddOnDetails, AutoAddon, OmsOrderId,IsFromAddon )
