             SET @Status = 0;
             SELECT 1 AS ID,
                    CAST(0 AS BIT) AS [Status],
                    @ErrorMessage;
             ROLLBACK TRAN UpdateAttributeValue;
             EXEC Znode_InsertProcedureErrorLog
                  @ProcedureName = 'Znode_UpdateAttributeValue',
                  @ErrorInProcedure = @Error_procedure,
                  @ErrorMessage = @ErrorMessage,
                  @ErrorLine = @ErrorLine,
                  @ErrorCall = @ErrorCall;
