										   WHERE LocaleId = '+CAST(@LocaleId AS VARCHAR(50))+'
										   UNION All 
										   SELECT M.PimProductId,M.AttributeCode,M.AttributeValue ,M.PimAttributeId,M.CreatedDate,M.ModifiedDate,M.DisplayOrder
										   FROM Cte_CollectData M 
