AS 
   /* 
     Summary : get tax with product name and other details 
     Sequence For Delete Data 
	 SELECT * FROM ZnodeTaxClassSKU
     EXEC Znode_GetTaxlClassDetail  '' , @RowsCount = 1  ,@TaxClassId = -1 

   */
     BEGIN
