             (PimProductId   INT,
              AttributeValue NVARCHAR(MAX),
              AttributeCode  VARCHAR(600),
              PimAttributeId INT
             );
               IF @TaxClassId <> 0
                 BEGIN
				insert into @TransferPimProductId 
