			 CurrencyName VARCHAR(200),ActivationDate DATETIME,ExpirationDate DATETIME,Precedence INT, RowId INT, CountNo INT 
			 )

             --SET @PageNo = CASE
             --                  WHEN @PageNo = 0
             --                  THEN @PageNo
             --                  ELSE(@PageNo - 1) * @Rows
             --              END;
             IF @Mode = ''
                 BEGIN
						SET @SQL = ' With PriceList AS 
						(SELECT zpl.ListCode,zpl.ListName,zc.CurrencyName + CASE WHEN isnull(zc.Symbol,'''') = '''' THEN '''' ELSE '' - '' + ISNULL(zc.Symbol,'''')  END  CurrencyName
					    ,zpl.PriceListId ,zpl.ActivationDate,zpl.ExpirationDate ,zpl.ModifiedDate
						FROM ZnodePriceList AS zpl INNER JOIN dbo.ZnodeCurrency zc ON zpl.CurrencyId = zc.CurrencyId)
						  
