				 WHERE  (ZS.StateCode =  '''+@statecode+''' OR ZS.StateName = '''+@statecode+''' OR  '''+@statecode+ ''' = ''''	)			 				
				 ) 
				 , Cte_GetStore AS 
				 (
				  SELECT  * ,'+dbo.Fn_GetPagingRowId(@Order_BY,'PortalId DESC')+',Count(*)Over() CountNo
				  FROM Cte_GetStoreDetails
				  WHERE 1=1 
				  '+dbo.Fn_GetFilterWhereClause(@WhereClause)+'
				 )

				 SELECT DISTINCT StoreName,CityName,StateName,PhoneNumber,PostalCode,Status,PortalAddressId,AddressId,PortalId,IsActive,DisplayOrder,Address1,Address2,Address3,CountryName,RowId,CountNo,PortalName,MediaPath,FileName
