												,CardNumber NVARCHAR(600),CreatedDate DATETIME, ExpirationDate DATETIME
												,Amount NUMERIC(28,6),CustomerId INT,CustomerName NVARCHAR(512),GiftCardId INT,UserId INT,CurrencyCode VARCHAR(100), RowId INT, CountNo INT  )

