CREATE PROCEDURE [dbo].[Znode_GetPublishSingleProduct]
(

    @PimProductId     VARCHAR(max)  ,
    @UserId            INT				 

)
AS
    
/*
  Summary :	Publish Product on the basis of publish catalog
				Retrive all Product details with attributes and insert into following tables 
				1.	ZnodePublishedXml
				2.	ZnodePublishCategoryProduct
				3.	ZnodePublishProduct
				4.	ZnodePublishProductDetail

                Product details include all the type of products link, grouped, configure and bundel products (include addon) their associated products 
				collect their attributes and values into tables variables to process for publish.  
                
				Finally genrate XML for products with their attributes and inserted into ZnodePublishedXml Znode Admin process xml from sql server to mongodb
				one by one.

     Unit Testing
    
     SELECT * FROM ZnodePimCustomField WHERE CustomCode = 'Test'
     SELECT * FROM ZnodePimCatalogCategory WHERE pimCatalogId = 3 AND PimProductId = 181
     SELECT * FROM ZnodePimCustomFieldLocale WHERE PimCustomFieldId = 1
	 SELECT * FROM ZnodePublishProduct WHERE PublishProductid = 213 = 30
     select * from znodepublishcatalog
	  SELECT * FROM view_loadmanageProduct WHERE Attributecode = 'ProductNAme' AND AttributeValue LIKE '%Apple%'
     SELECT * FROM ZnodePimCategoryProduct WHERE  PimProductId = 181
	 SELECT * FROM ZnodePimCatalogcategory WHERE pimcatalogId = 3 
     EXEC Znode_GetPublishProducts  @PublishCatalogId = NULL ,@UserId= 2 ,@NotReturnXML= NULL,@PimProductId = 117,@IsDebug= 1 
     EXEC Znode_GetPublishProducts  @PublishCatalogId = 3,@UserId= 2 ,@NotReturnXML= NULL,@IsDebug= 1  ,@PimProductId = 2151
     EXEC Znode_GetPublishSingleProduct  @PublishCatalogId =1,@UserId= 2 ,@RequiredXML= 1	
	 SELECT * FROM 	ZnodePimCatalogCategory  WHERE pimcatalogId = 3  
	 SELECT * FROM [dbo].[ZnodePimCategoryHierarchy]  WHERE pimcatalogId = 3 
    */ 

     BEGIN
        
         BEGIN TRY
             SET NOCOUNT ON;
			 DECLARE @GetDate DATETIME = dbo.Fn_GetDate(); 
			 DECLARE @PimAttributeId VARCHAR(max) = ''
			-- DECLARE @PimCatalogId int= ISNULL((SELECT PimCatalogId FROM ZnodePublishcatalog WHERE PublishCatalogId = @PublishCatalogId), 0);  --- this variable is used to carry y pim catalog id by using published catalog id
            
			 -- This variable used to carry the locale in loop 
			 -- This variable is used to carry the default locale which is globaly set

			 DECLARE @LocaleId INT =Dbo.Fn_GetDefaultLocaleId()
					,@DefaultLocaleId INT= Dbo.Fn_GetDefaultLocaleId()
					,@ProductNamePimAttributeId INT = dbo.Fn_GetProductNameAttributeId()
					,@SkuPimAttributeId  INT =  dbo.Fn_GetProductSKUAttributeId() 
					,@IsActivePimAttributeId INT =  dbo.Fn_GetProductIsActiveAttributeId()
			
			DECLARE @TBL_AttributesDetails TABLE(PimAttributeId int , AttributeCode varchar(300), IsUseInSearch bit, IsHtmlTags bit
										, IsComparable bit, IsFacets bit, AttributeValue varchar(max), IsLinkAttribute bit, PimProductId int
										,PRIMARY KEY (AttributeCode,PimProductId,IsLinkAttribute)) 
			 -- This variable is used in loop to increment the counter
             DECLARE @TBL_PimProductIds TABLE(PimProductId INT ,IsParentProducts BIT
									,ProductName NVARCHAR(MAX),SKU  NVARCHAR(MAX),IsActive NVARCHAR(MAX),PimAttributeFamilyId INT,LocaleId INT 
									,PRIMARY KEY (PimProductId,LocaleId) );
             DECLARE @TBL_AttributeValue TABLE(PimProductId int, AttributeValue nvarchar(max), AttributeCode varchar(300), PimAttributeId int ,DefaultValueXML NVARCHAR(max),LocaleId INT 
			    PRIMARY KEY (PimProductId,AttributeCode));
            
			 DECLARE @TBL_CustomeFiled TABLE( PimProductId int, CustomCode varchar(300), CustomKey nvarchar(max), CustomKeyValue nvarchar(max),PRIMARY KEY (PimProductId,CustomCode));

			  DECLARE @TBL_PimAttributeIds TABLE(PimAttributeId int, ParentPimAttributeId int, AttributeTypeId int, AttributeCode varchar(300)
									, IsRequired bit, IsLocalizable bit, IsFilterable bit, IsSystemDefined bit, IsConfigurable bit, IsPersonalizable bit
									, DisplayOrder int, HelpDescription varchar(max), IsCategory bit, IsHidden bit, CreatedDate datetime, ModifiedDate datetime,
									 AttributeName nvarchar(max), AttributeTypeName varchar(300), IsCustomeField bit);

		   --	INSERT INTO @TBL_AttributeValue(PimProductId, AttributeValue,AttributeCode,PimAttributeId )
			EXEC [dbo].[Znode_GetProductsAttributeValueWithCode] @PimProductId,'',@localeId , 0
		  	  
		  
		    INSERT INTO @TBL_AttributesDetails (PimAttributeId,AttributeCode,IsComparable,IsUseInSearch,IsHtmlTags,IsFacets,IsLinkAttribute,PimProductId)
				SELECT ZPA.PimAttributeId,ZPA.AttributeCode,IsComparable,IsUseInSearch,IsHtmlTags,IsFacets,0,0
				FROM ZnodePimAttribute AS ZPA 
				INNER JOIN @TBL_AttributeValue ZPAV ON (ZPAV.PimAttributeId = ZPA.PimAttributeId )
				INNER JOIN ZnodePimProduct ZPP ON (ZPP.PimProductid=  ZPAV.PimProductId )
				LEFT JOIN ZnodePimFrontendProperties AS ZPFP ON ZPFP.PimAttributeId = ZPA.PimATtributeId
				WHERE (EXISTS (SELECT TOP 1 1 FROM ZnodePimFamilyGroupMapper AS ZPFGM WHERE ZPFGM.PimAttributeId = ZPA.PimAttributeId 
				AND ZPFGM.PimAttributeFamilyId = ZPP.PimAttributeFamilyId)
				OR ZPA.IsPersonalizable = 1)
				AND IsCategory = 0 
				GROUP BY ZPA.PimAttributeId,ZPA.AttributeCode,IsComparable,IsUseInSearch,IsHtmlTags,IsFacets
				
				UNION ALL
				
				SELECT ZPLPD.PimAttributeId,ZPA.AttributeCode,IsComparable,IsUseInSearch,IsHtmlTags,IsFacets,1,PimParentProductId
				FROM ZnodePIMAttribute AS ZPA 
				INNER JOIN ZnodePimLinkProductDetail AS ZPLPD ON ZPLPD.pimAttributeId = ZPA.PimATtributeId
				LEFT JOIN ZnodePimFrontendProperties AS ZPFP ON ZPFP.PimAttributeId = ZPLPD.PimATtributeId 
				WHERE EXISTS (
				SELECT TOP 1 1
				FROM @TBL_AttributeValue AS TBPP
				WHERE TBPP.PimProductId = ZPLPD.PimParentProductId
				)
				AND IsCategory = 0
				GROUP BY ZPLPD.PimAttributeId,
				AttributeCode,
				IsComparable,
				IsUseInSearch,
				IsHtmlTags,
				IsFacets,
				PimParentProductId;
			
			SET @PimAttributeId = SUBSTRING((SELECT ','+CAST(PimAttributeId AS VARCHAr(50)) FROM @TBL_AttributesDetails FOR XML PATH ('') ),2,4000)

			INSERT INTO @TBL_PimAttributeIds ( PimAttributeId,ParentPimAttributeId,AttributeTypeId,AttributeCode,IsRequired,IsLocalizable,IsFilterable,IsSystemDefined,
                      IsConfigurable,IsPersonalizable,DisplayOrder,HelpDescription,IsCategory,IsHidden,CreatedDate,ModifiedDate,AttributeName,AttributeTypeName )
			EXEC Znode_GetPimAttributesDetails @PimAttributeId,@LocaleId

			INSERT INTO @TBL_CustomeFiled (PimProductId,CustomCode,CustomKey,CustomKeyValue)
			EXEC [dbo].[Znode_GetCustomeFileldLocale] @PimProductId,@LocaleId

			INSERT INTO @TBL_AttributeValue (PimProductId, AttributeValue,AttributeCode,PimAttributeId)
			SELECT PimProductId, CustomKeyValue,CustomCode,NULL
			FROM @TBL_CustomeFiled

			INSERT INTO @TBL_PimAttributeIds (AttributeCode,IsCustomeField,AttributeName)
            SELECT CustomCode,1,CustomKey
            FROM @TBL_CustomeFiled 


			  SELECT  TBAVI.PimProductId , TBAVI.AttributeCode ,
                              (
                                   SELECT DISTINCT TBAV.AttributeCode,
                                          AttributeName,
                                          ISNULL(IsUseInSearch, 0) AS IsUseInSearch,
                                          ISNULL(IsHtmlTags, 0) AS IsHtmlTags,
                        ISNULL(IsComparable, 0) AS IsComparable,
                                          ISNULL(IsFacets, 0) AS IsFacets,
                                          ISNULL(TBAV.AttributeValue,'') AS AttributeValues,
                                          AttributeTypeName,
                                          IsPersonalizable,
                                          ISNULL(TBA.IsCustomeField, 0) AS IsCustomeField,
                                          ISNULL(IsConfigurable, 0) AS IsConfigurable
										  
                                   FROM @TBL_AttributeValue AS TBAV
                                        INNER JOIN @TBL_PimAttributeIds AS TBA ON TBA.AttributeCode = TBAV.AttributeCode
                                        INNER JOIN @TBL_AttributesDetails AS TBAD ON TBAD.AttributeCode = TBAV.AttributeCode
                                                                                     AND (TBAD.PimProductId = TBAV.PimProductId
                                                                                          OR TBAD.IsLinkAttribute = 0)
									--	INNER JOIN @TBL_PimProductIdToCalculate b ON (b.PimProductId = TBA.PimProductId)
                                   WHERE TBA.AttributeCode = TBAVI.AttributeCode
                                         AND TBAV.PimProductId = TBAVI.PimProductId
										
                                   FOR XML PATH('AttributeEntity')
                               )
                               FROM @TBL_AttributeValue AS TBAVI

		    	

		


		END  TRY 
		BEGIN CATCH 
		SELECT ERROR_MESSAGE()
		END CATCH 
					
     END
GO
